import DrawingCanvas from './components/DrawingCanvas'
import HttpsProxyAgent from 'https-proxy-agent';
import axios from 'axios';


class DigitRecognizer extends React.Component {
  static async getInitialProps(){
    const config = {
       httpsAgent: new HttpsProxyAgent(process.env.https_proxy),
       host: '127.0.0.1',
       port: 9000
     }
     const res = await axios.get('https://api.tvmaze.com/search/shows?q=batman', config);
     const data = await res.json();

     console.log(`Show data fetched. Count: ${data.length}`);

     return {
       shows: data.map(entry => entry.show)
     };
  };
  render(){
    return (
      <div className="App">
        <h1>Draw any digit from 0-9</h1>
        <DrawingCanvas/>
        <style >{`
          .App{
            text-align: center;
          }
          .button-wrapper{
            padding-top: 5vh;
          }
          .signatureCanvas{
            border: solid gray;
          }
          `}</style>
      </div>
    );
  }
}

export default DigitRecognizer;
