import DrawingCanvas from './components/DrawingCanvas'
import axios from 'axios'

class DigitRecognizer extends React.Component {
  static async getInitialProps(ctx){
    const config = {
       proxy: {
         host: '127.0.0.1',
         port: 9000,
         auth: {
           username: 'mikeymike',
           password: 'rapunz3l'
         }
       }
     }
     const res = await axios.get('https://api.tvmaze.com/search/shows?q=batman', config);
     const data = await res.json();
     console.log(`Show data fetched. Count: ${data.length}`);
  }
  render(){
    return (
      <div className="App">
        <h1>Draw any digit from 0-9</h1>
        <DrawingCanvas/>
        <style >{`
          .App{
            text-align: center;
          }
          .button-wrapper{
            padding-top: 5vh;
          }
          .signatureCanvas{
            border: solid gray;
          }
          `}</style>
      </div>
    );
  }
}

export default DigitRecognizer;
