const ScrollPanel = props => (
  <div className="wrapper">
    <div className="title">{props.title}</div>
    <div className="content">{props.children}</div>
    <style jsx>{`
      .wrapper{
        min-height: 100vh;
        height: auto;
        font-weight : lighter;
        font-size: 30px;
        font-size: 4vh;
        padding-left: 2vw;

        animation-name: example;
        animation-duration: 4s;
      }
      .title {
        font-weight : lighter;
        font-size: 30px;
        font-size: 4vh;
        padding-left: 2vw;
      }


      @keyframes example {
        0%   {opacity: 0}
        25%  {opacity: 0.25}
        50%  {opacity: 0.5}
        100% {opacity: 1}
      }
      `}</style>
  </div>
)

export default ScrollPanel
