import Markdown from 'react-markdown';

export default function Layout(props) {
  return (
    <div>
      <div className="markdown">
          <Markdown source={props.children} />
      </div>
      <style jsx global>{`
          .markdown {
            font-family: 'Arial';
          }

          .markdown a {
            text-decoration: none;
            color: blue;
          }

          .markdown a:hover {
            opacity: 0.6;
          }

          .markdown h3 {
            margin: 0;
            padding: 0;
            text-transform: uppercase;
          }
        `}</style>
      </div>
    )
  }
