import Link from './HeaderLink'
import { useRouter } from 'next/router';

export default function Header() {
  const router = useRouter();
  return (
    <div>
      <Link href="/" className={router.pathname == "/" ? "active" : ""}>
        <a>About</a>
      </Link>
      <Link href="/projects" className={router.pathname == "/projects" ? "active" : ""}>
        <a>Projects</a>
      </Link>
      <Link href="/designs">
        <a>Designs</a>
      </Link>
      <Link href="/contact">
        <a>Contact</a>
      </Link>
      <style jsx>{`
        padding: 2rem;


        .active {
          background: black;
          font-weight: bold;
          color: white;
        }

        .active:after {
                content: ' (current page)';
              }


        a {
          text-decoration: none;
          color: #3B3A3A;
        }
        a:active {
          font-weight: bold;
        }
        a:hover {
          opacity: 0.6;
        }

        `}</style>
    </div>
  )
}
